on(release) {
	 if(_currentframe == 1) { //pause button
		 this.nextFrame();

	 	//add this line everytime the gameplay is stopped
		 PokiSDK2.gameplayStop();

	}
	 else { //resume button
		 this.gotoAndStop(1);

	 	 //add this line everytime the gameplay is resumed
		 PokiSDK2.gameplayStart();
	}
}
